import { useEffect, useState } from "react";
import axios from "axios";
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, PieChart, Pie, Cell } from 'recharts';

function Dashboard() {
  const [ingresos, setIngresos] = useState([]);
  const [gastos, setGastos] = useState([]);
  const [balance, setBalance] = useState({ ingresos: 0, gastos: 0, balance: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [ingresosRes, gastosRes, balanceRes] = await Promise.all([
          axios.get("http://127.0.0.1:8000/ingresos"),
          axios.get("http://127.0.0.1:8000/gastos"),
          axios.get("http://127.0.0.1:8000/balance")
        ]);
        setIngresos(ingresosRes.data);
        setGastos(gastosRes.data);
        setBalance(balanceRes.data);
      } catch (err) {
        console.error("Error al obtener datos:", err);
        setError("No se pudieron cargar los datos.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <div style={styles.container}>Cargando dashboard...</div>;
  if (error) return <div style={styles.container}>‚ùå {error}</div>;

  // Datos para gr√°ficos
  const dataPorTipo = [
    { name: 'Ingresos', value: balance.ingresos },
    { name: 'Gastos', value: balance.gastos }
  ];
  const COLORS = ['#4caf50', '#f44336'];

  // Combinar y ordenar movimientos
  const movimientos = [
    ...ingresos.map(i => ({ ...i, type: 'Ingreso' })),
    ...gastos.map(g => ({ ...g, type: 'Gasto' }))
  ].sort((a, b) => new Date(b.fecha) - new Date(a.fecha));

  const ultimosMovimientos = movimientos.slice(0, 5);

  return (
    <div style={styles.container}>
      <h1>üí∞ MiBalance - Dashboard</h1>

      {/* Tarjetas de resumen */}
      <div style={styles.cards}>
        <div style={{ ...styles.card, background: '#e8f5e8' }}>
          <h3>Ingresos</h3>
          <p style={styles.ingresos}>${balance.ingresos.toLocaleString()}</p>
        </div>
        <div style={{ ...styles.card, background: '#ffe6e6' }}>
          <h3>Gastos</h3>
          <p style={styles.gastos}>${balance.gastos.toLocaleString()}</p>
        </div>
        <div style={{ ...styles.card, background: '#e3f2fd' }}>
          <h3>Balance</h3>
          <p style={{ ...styles.balance, color: balance.balance >= 0 ? 'green' : 'red' }}>
            ${balance.balance.toLocaleString()}
          </p>
        </div>
      </div>

      {/* Gr√°ficas */}
      <div style={styles.chartsContainer}>
        <div style={styles.chartCard}>
          <h3>Ingresos vs Gastos</h3>
          <BarChart width={400} height={300} data={dataPorTipo}>
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="value" fill="#8884d8" />
          </BarChart>
        </div>
        <div style={styles.chartCard}>
          <h3>Distribuci√≥n</h3>
          <PieChart width={300} height={300}>
            <Pie
              data={dataPorTipo}
              cx="50%"
              cy="50%"
              outerRadius={80}
              fill="#8884d8"
              dataKey="value"
              label
            >
              {dataPorTipo.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
          </PieChart>
        </div>
      </div>

      {/* √öltimos movimientos */}
      <div style={styles.ultimos}>
        <h2>√öltimos Movimientos</h2>
        {ultimosMovimientos.length === 0 ? (
          <p>No hay movimientos recientes.</p>
        ) : (
          <ul style={styles.list}>
            {ultimosMovimientos.map(m => (
              <li key={m.id} style={styles.listItem}>
                <span style={m.type === 'Ingreso' ? styles.ingresoIcon : styles.gastoIcon}>
                  {m.type === 'Ingreso' ? '+' : '-'}
                </span>
                <div style={styles.itemContent}>
                  <strong>${m.monto}</strong> - {m.description || m.descripcion}
                  <br />
                  <small>{new Date(m.fecha).toLocaleDateString('es-ES')}</small>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

// Estilos (igual que en las otras p√°ginas)
const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    maxWidth: '1200px',
    margin: '0 auto'
  },
  cards: {
    display: 'flex',
    gap: '20px',
    marginBottom: '30px',
    flexWrap: 'wrap'
  },
  card: {
    flex: '1 1 200px',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    textAlign: 'center'
  },
  ingresos: {
    fontSize: '24px',
    color: 'green',
    fontWeight: 'bold'
  },
  gastos: {
    fontSize: '24px',
    color: 'red',
    fontWeight: 'bold'
  },
  balance: {
    fontSize: '24px',
    fontWeight: 'bold'
  },
  chartsContainer: {
    display: 'flex',
    gap: '20px',
    flexWrap: 'wrap',
    marginBottom: '30px'
  },
  chartCard: {
    flex: '1 1 400px',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    textAlign: 'center'
  },
  ultimos: {
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
  },
  list: {
    listStyle: 'none',
    padding: 0
  },
  listItem: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: '10px',
    padding: '10px 0',
    borderBottom: '1px solid #eee'
  },
  ingresoIcon: {
    color: 'green',
    fontWeight: 'bold',
    fontSize: '20px',
    width: '20px',
    textAlign: 'center'
  },
  gastoIcon: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '20px',
    width: '20px',
    textAlign: 'center'
  },
  itemContent: {
    flex: 1
  }
};

export default Dashboard;
