import { useEffect, useState } from "react";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, Legend,
  PieChart, Pie, Cell, ResponsiveContainer
} from 'recharts';

function Dashboard() {
  const [ingresos, setIngresos] = useState([]);
  const [gastos, setGastos] = useState([]);
  const [recordatorios, setRecordatorios] = useState([]);
  const [balance, setBalance] = useState({ ingresos: 0, gastos: 0, balance: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [ingresosRes, gastosRes, balanceRes, recordatoriosRes] = await Promise.all([
          axios.get("http://127.0.0.1:8000/ingresos"),
          axios.get("http://127.0.0.1:8000/gastos"),
          axios.get("http://127.0.0.1:8000/balance"),
          axios.get("http://127.0.0.1:8000/recordatorios")
        ]);
        setIngresos(ingresosRes.data);
        setGastos(gastosRes.data);
        setBalance(balanceRes.data);
        setRecordatorios(recordatoriosRes.data);
      } catch (err) {
        console.error("Error al obtener datos:", err);
        setError("No se pudieron cargar los datos.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Alternar estado completado con persistencia
  const handleToggleCompletado = async (id, completadoActual) => {
    try {
      await axios.patch(`http://127.0.0.1:8000/recordatorios/${id}`, {
        completado: !completadoActual
      });
      // Recargar recordatorios para reflejar cambio
      const res = await axios.get("http://127.0.0.1:8000/recordatorios");
      setRecordatorios(res.data);
    } catch (err) {
      console.error("Error al actualizar recordatorio:", err);
      alert("No se pudo actualizar el estado");
    }
  };

  const handleDeleteRecordatorio = async (id) => {
    if (!window.confirm('¬øEliminar este recordatorio?')) return;
    try {
      await axios.delete(`http://127.0.0.1:8000/recordatorios/${id}`);
      const res = await axios.get("http://127.0.0.1:8000/recordatorios");
      setRecordatorios(res.data);
    } catch (err) {
      console.error('Error al eliminar recordatorio:', err);
      alert('No se pudo eliminar el recordatorio');
    }
  };

  if (loading) return <div style={styles.container}>Cargando dashboard...</div>;
  if (error) return <div style={styles.container}>‚ùå {error}</div>;

  // Datos para gr√°ficos
  const dataPorTipo = [
    { name: 'Ingresos', value: balance.ingresos },
    { name: 'Gastos', value: balance.gastos }
  ];
  const COLORS = ['#4caf50', '#f44336'];

  const formatoMoneda = (value) => 
    value.toLocaleString('es-ES', { style: 'currency', currency: 'USD', minimumFractionDigits: 0 });

  // Combinar y ordenar movimientos
  const movimientos = [
    ...ingresos.map(i => ({ ...i, type: 'Ingreso' })),
    ...gastos.map(g => ({ ...g, type: 'Gasto' }))
  ].sort((a, b) => new Date(b.fecha) - new Date(a.fecha));

  const ultimosMovimientos = movimientos.slice(0, 5);

  return (
    <div style={styles.container}>
      <h1>üí∞ MiBalance - Dashboard</h1>

      {/* Tarjetas de resumen */}
      <div style={styles.cards}>
        <div style={{ ...styles.card, background: '#e8f5e8' }}>
          <h3 style={styles.cardTitle}>Ingresos</h3>
          <p style={styles.ingresos}>{formatoMoneda(balance.ingresos)}</p>
        </div>
        <div style={{ ...styles.card, background: '#ffe6e6' }}>
          <h3 style={styles.cardTitle}>Gastos</h3>
          <p style={styles.gastos}>{formatoMoneda(balance.gastos)}</p>
        </div>
        <div style={{ ...styles.card, background: '#e3f2fd' }}>
          <h3 style={styles.cardTitle}>Balance</h3>
          <p style={{ ...styles.balance, color: balance.balance >= 0 ? '#2e7d32' : '#c62828' }}>
            {formatoMoneda(balance.balance)}
          </p>
        </div>
      </div>

      {/* Gr√°ficas */}
      <div style={styles.chartsContainer}>
        <div style={styles.chartCard}>
          <h3>Ingresos vs Gastos</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={dataPorTipo}>
              <XAxis dataKey="name" />
              <YAxis tickFormatter={(value) => formatoMoneda(value)} />
              <Tooltip formatter={(value) => formatoMoneda(value)} />
              <Legend />
              <Bar dataKey="value" fill="#8884d8">
                {dataPorTipo.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div style={styles.chartCard}>
          <h3>Distribuci√≥n</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={dataPorTipo}
                cx="50%"
                cy="50%"
                outerRadius={100}
                fill="#8884d8"
                dataKey="value"
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(1)}%`}
              >
                {dataPorTipo.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip formatter={(value) => formatoMoneda(value)} />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* √öltimos movimientos */}
      <div style={styles.ultimos}>
        <h2>√öltimos Movimientos</h2>
        {ultimosMovimientos.length === 0 ? (
          <p>No hay movimientos recientes.</p>
        ) : (
          <ul style={styles.list}>
            {ultimosMovimientos.map(m => (
              <li key={m.id} style={styles.listItem}>
                <span style={m.type === 'Ingreso' ? styles.ingresoIcon : styles.gastoIcon}>
                  {m.type === 'Ingreso' ? '+' : '-'}
                </span>
                <div style={styles.itemContent}>
                  <strong>{formatoMoneda(m.monto)}</strong> - {m.description || m.descripcion}
                  <br />
                  <small>{new Date(m.fecha).toLocaleDateString('es-ES')}</small>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Todos los recordatorios (pendientes y completados) */}
      <div style={styles.ultimos}>
        <h2>üìÖ Recordatorios</h2>
        {recordatorios.length === 0 ? (
          <p>No hay recordatorios.</p>
        ) : (
          <ul style={styles.list}>
            {recordatorios.map(r => (
              <li key={r.id} style={styles.listItem}>
                <input
                  type="checkbox"
                  checked={r.completado || false}
                  onChange={() => handleToggleCompletado(r.id, r.completado)}
                  style={{ marginRight: '10px' }}
                />
                <div style={{
                  ...styles.itemContent,
                  textDecoration: r.completado ? 'line-through' : 'none',
                  opacity: r.completado ? 0.6 : 1,
                  color: r.completado ? '#999' : 'inherit'
                }}>
                  <strong>{r.titulo}</strong>
                  {r.monto && <span> - {formatoMoneda(r.monto)}</span>}
                  <br />
                  <small>üìÖ {new Date(r.fecha).toLocaleDateString('es-ES')}</small>
                </div>
                <button
                  onClick={() => handleDeleteRecordatorio(r.id)}
                  style={styles.deleteButton}
                  title="Eliminar"
                >
                  üóëÔ∏è
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

