import { useState, useEffect } from 'react';
import axios from 'axios';

function IngresosPage() {
  const [ingresos, setIngresos] = useState([]);
  const [monto, setMonto] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    cargarIngresos();
  }, []);

  const cargarIngresos = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:8000/ingresos');
      setIngresos(res.data);
    } catch (error) {
      console.error('Error al cargar ingresos:', error);
      alert('No se pudieron cargar los ingresos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('http://127.0.0.1:8000/ingresos', {
        monto: parseFloat(monto),
        description: descripcion
      });
      await cargarIngresos();
      setMonto('');
      setDescripcion('');
    } catch (error) {
      console.error('Error al guardar ingreso:', error);
      alert('Ocurri贸 un error al guardar el ingreso');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1> MiBalance - Ingresos</h1>

      {/* Tarjeta de formulario */}
      <div style={styles.card}>
        <h2>Registrar Ingreso</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.field}>
            <label>Monto:</label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={monto}
              onChange={(e) => setMonto(e.target.value)}
              required
              style={styles.input}
              placeholder="Ingrese monto"
            />
          </div>
          <div style={styles.field}>
            <label>Descripci贸n:</label>
            <input
              type="text"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
              style={styles.input}
              placeholder="Descripci贸n del ingreso"
            />
          </div>
          <button type="submit" disabled={loading} style={styles.button}>
            {loading ? 'Guardando...' : 'Guardar Ingreso'}
          </button>
        </form>
      </div>

      {/* Tarjeta de lista de ingresos */}
      <div style={styles.card}>
        <h2>Lista de Ingresos</h2>
        {ingresos.length === 0 ? (
          <p>No hay ingresos registrados.</p>
        ) : (
          <ul style={styles.list}>
            {ingresos.map(ingreso => (
              <li key={ingreso.id} style={styles.listItem}>
                <span style={styles.ingresoIcon}>+</span>
                <div style={styles.itemContent}>
                  <strong>${ingreso.monto}</strong> - {ingreso.description}
                  <br />
                  <small style={styles.date}>
                    {new Date(ingreso.fecha).toLocaleDateString('es-ES')}
                  </small>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

// Estilos consistentes con los otros componentes
const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    maxWidth: '800px',
    margin: '0 auto'
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    padding: '20px',
    marginBottom: '30px'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  field: {
    display: 'flex',
    flexDirection: 'column',
    gap: '5px'
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '4px'
  },
  button: {
    padding: '12px',
    fontSize: '16px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '10px'
  },
  list: {
    listStyle: 'none',
    padding: 0
  },
  listItem: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: '10px',
    padding: '10px 0',
    borderBottom: '1px solid #eee'
  },
  ingresoIcon: {
    color: 'green',
    fontWeight: 'bold',
    fontSize: '20px',
    width: '20px',
    textAlign: 'center'
  },
  itemContent: {
    flex: 1
  },
  date: {
    color: '#666'
  }
};

export default IngresosPage;
