      flex: 1,
    },
    date: {
      color: isDark ? '#aaa' : '#666',
    },
    deleteButton: {
      background: 'none',
      border: 'none',
      cursor: 'pointer',
      fontSize: '1.2rem',
      padding: '0 5px',
      color: isDark ? '#ff8a80' : '#c62828',
    },
    textColor: isDark ? '#e0e0e0' : '#333333',
