import { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  // Cerrar men√∫ al seleccionar una opci√≥n
  const handleLinkClick = () => {
    setIsOpen(false);
  };

  // Opcional: cerrar men√∫ al hacer click fuera (podr√≠a implementarse con useRef y useEffect, pero por simplicidad lo dejamos as√≠)

  return (
    <nav className="navbar">
      <div className="navbar-brand">üí∞ MiBalance</div>
      <button className="navbar-toggle" onClick={() => setIsOpen(!isOpen)}>
        ‚ò∞
      </button>
      <ul className={`navbar-links ${isOpen ? 'open' : ''}`}>
        <li>
          <NavLink to="/" className={({ isActive }) => (isActive ? 'active' : '')} onClick={handleLinkClick}>
            Dashboard
          </NavLink>
        </li>
        <li>
          <NavLink to="/ingresos" className={({ isActive }) => (isActive ? 'active' : '')} onClick={handleLinkClick}>
            Ingresos
          </NavLink>
        </li>
        <li>
          <NavLink to="/gastos" className={({ isActive }) => (isActive ? 'active' : '')} onClick={handleLinkClick}>
            Gastos
          </NavLink>
        </li>
        <li>
          <NavLink to="/recordatorios" className={({ isActive }) => (isActive ? 'active' : '')} onClick={handleLinkClick}>
            Recordatorios
          </NavLink>
        </li>
      </ul>
    </nav>
  );
}

export default Navbar;
