import { useState, useEffect } from 'react';
import axios from 'axios';

function GastosPage() {
  const [gastos, setGastos] = useState([]);
  const [monto, setMonto] = useState('');
  const [categoria, setCategoria] = useState('Comida'); // valor por defecto
  const [descripcion, setDescripcion] = useState('');
  const [loading, setLoading] = useState(false);

  // Categor铆as predefinidas (puedes ampliarlas)
  const categorias = [
    'Comida',
    'Transporte',
    'Entretenimiento',
    'Salud',
    'Educaci贸n',
    'Vivienda',
    'Servicios',
    'Otros'
  ];

  useEffect(() => {
    cargarGastos();
  }, []);

  const cargarGastos = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:8000/gastos');
      setGastos(res.data);
    } catch (error) {
      console.error('Error al cargar gastos:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('http://127.0.0.1:8000/gastos', {
        monto: parseFloat(monto),
        categoria,
        description: descripcion
      });
      // Recargar lista
      await cargarGastos();
      // Limpiar formulario
      setMonto('');
      setCategoria('Comida');
      setDescripcion('');
    } catch (error) {
      console.error('Error al guardar gasto:', error);
      alert('Ocurri贸 un error al guardar el gasto');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1> MiBalance - Gastos</h1>

      {/* Formulario de registro */}
      <div style={styles.card}>
        <h2>Registrar Gasto</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.field}>
            <label>Monto:</label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={monto}
              onChange={(e) => setMonto(e.target.value)}
              required
              style={styles.input}
              placeholder="Ingrese monto"
            />
          </div>

          <div style={styles.field}>
            <label>Categor铆a:</label>
            <select
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
              style={styles.input}
            >
              {categorias.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div style={styles.field}>
            <label>Descripci贸n:</label>
            <input
              type="text"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
              style={styles.input}
              placeholder="Descripci贸n del gasto"
            />
          </div>

          <button type="submit" disabled={loading} style={styles.button}>
            {loading ? 'Guardando...' : 'Guardar Gasto'}
          </button>
        </form>
      </div>

      {/* Lista de gastos */}
      <div style={styles.card}>
        <h2>Lista de Gastos</h2>
        {gastos.length === 0 ? (
          <p>No hay gastos registrados.</p>
        ) : (
          <ul style={styles.list}>
            {gastos.map(gasto => (
              <li key={gasto.id} style={styles.listItem}>
                <span style={styles.gastoIcon}>-</span>
                <div style={styles.itemContent}>
                  <strong>${gasto.monto}</strong> - {gasto.categoria} - {gasto.description}
                  <br />
                  <small style={styles.date}>
                    {new Date(gasto.fecha).toLocaleDateString('es-ES')}
                  </small>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    maxWidth: '800px',
    margin: '0 auto'
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    padding: '20px',
    marginBottom: '30px'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  field: {
    display: 'flex',
    flexDirection: 'column',
    gap: '5px'
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '4px'
  },
  button: {
    padding: '12px',
    fontSize: '16px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '10px'
  },
  list: {
    listStyle: 'none',
    padding: 0
  },
  listItem: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: '10px',
    padding: '10px 0',
    borderBottom: '1px solid #eee'
  },
  gastoIcon: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: '20px',
    width: '20px',
    textAlign: 'center'
  },
  itemContent: {
    flex: 1
  },
  date: {
    color: '#666'
  }
};

export default GastosPage;
