import { useState, useEffect } from 'react';
import axios from 'axios';

function IngresosPage() {
  const [ingresos, setIngresos] = useState([]);
  const [monto, setMonto] = useState('');
  const [descripcion, setDescripcion] = useState('');

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/ingresos')
      .then(res => setIngresos(res.data))
      .catch(console.error);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:8000/ingresos', { monto: parseFloat(monto), description: descripcion })
      .then(() => {
        // Recargar lista
        return axios.get('http://127.0.0.1:8000/ingresos');
      })
      .then(res => setIngresos(res.data))
      .catch(console.error);
    setMonto('');
    setDescripcion('');
  };

  return (
    <div>
      <h2>Registrar Ingreso</h2>
      <form onSubmit={handleSubmit}>
        <input type="number" placeholder="Monto" value={monto} onChange={e => setMonto(e.target.value)} required />
        <input type="text" placeholder="DescripciÃ³n" value={descripcion} onChange={e => setDescripcion(e.target.value)} required />
        <button type="submit">Guardar Ingreso</button>
      </form>
      <h3>Lista de Ingresos</h3>
      <ul>
        {ingresos.map(i => (
          <li key={i.id}>+ ${i.monto} - {i.description} <small>{new Date(i.fecha).toLocaleDateString()}</small></li>
        ))}
      </ul>
    </div>
  );
}
export default IngresosPage;
