import { useState, useEffect } from 'react';
import axios from 'axios';

function RecordatoriosPage() {
  const [recordatorios, setRecordatorios] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [fecha, setFecha] = useState('');
  const [monto, setMonto] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    cargarRecordatorios();
  }, []);

  const cargarRecordatorios = async () => {
    try {
      // Podr칤as usar /recordatorios/proximos si lo prefieres
      const res = await axios.get('http://127.0.0.1:8000/recordatorios');
      // Ordenar por fecha (m치s pr칩ximo primero)
      const ordenados = res.data.sort((a, b) => new Date(a.fecha) - new Date(b.fecha));
      setRecordatorios(ordenados);
    } catch (error) {
      console.error('Error al cargar recordatorios:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('http://127.0.0.1:8000/recordatorios', {
        titulo,
        fecha,
        monto: monto ? parseFloat(monto) : null
      });
      await cargarRecordatorios();
      setTitulo('');
      setFecha('');
      setMonto('');
    } catch (error) {
      console.error('Error al guardar recordatorio:', error);
      alert('Ocurri칩 un error al guardar el recordatorio');
    } finally {
      setLoading(false);
    }
  };

  // Funci칩n para formatear fecha en formato legible
  const formatearFecha = (fechaStr) => {
    const opciones = { day: '2-digit', month: 'short', year: 'numeric' };
    return new Date(fechaStr).toLocaleDateString('es-ES', opciones);
  };

  return (
    <div style={styles.container}>
      <h1>游눯 MiBalance - Recordatorios</h1>

      <div style={styles.card}>
        <h2>Nuevo Recordatorio</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.field}>
            <label>T칤tulo:</label>
            <input
              type="text"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
              required
              style={styles.input}
              placeholder="Ej. Pagar arriendo"
            />
          </div>

          <div style={styles.field}>
            <label>Fecha:</label>
            <input
              type="date"
              value={fecha}
              onChange={(e) => setFecha(e.target.value)}
              required
              style={styles.input}
            />
          </div>

          <div style={styles.field}>
            <label>Monto (opcional):</label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={monto}
              onChange={(e) => setMonto(e.target.value)}
              style={styles.input}
              placeholder="Ingrese monto si aplica"
            />
          </div>

          <button type="submit" disabled={loading} style={styles.button}>
            {loading ? 'Guardando...' : 'Guardar Recordatorio'}
          </button>
        </form>
      </div>

      <div style={styles.card}>
        <h2>Pr칩ximos Pagos / Recordatorios</h2>
        {recordatorios.length === 0 ? (
          <p>No hay recordatorios pendientes.</p>
        ) : (
          <ul style={styles.list}>
            {recordatorios.map(rec => (
              <li key={rec.id} style={styles.listItem}>
                <div style={styles.itemContent}>
                  <strong>{rec.titulo}</strong>
                  <br />
                  <span style={styles.date}>游늰 {formatearFecha(rec.fecha)}</span>
                  {rec.monto && (
                    <span style={styles.monto}> - 游눯 ${rec.monto}</span>
                  )}
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

// Reutilizamos los mismos estilos de GastosPage, a침adiendo algunos espec칤ficos
const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    maxWidth: '800px',
    margin: '0 auto'
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    padding: '20px',
    marginBottom: '30px'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  field: {
    display: 'flex',
    flexDirection: 'column',
    gap: '5px'
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '4px'
  },
  button: {
    padding: '12px',
    fontSize: '16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '10px'
  },
  list: {
    listStyle: 'none',
    padding: 0
  },
  listItem: {
    padding: '10px 0',
    borderBottom: '1px solid #eee'
  },
  itemContent: {
    flex: 1
  },
  date: {
    color: '#666',
    fontSize: '0.9em'
  },
  monto: {
    fontWeight: 'bold',
    color: '#28a745'
  }
};

export default RecordatoriosPage;
